import { useState } from "react";

export default function Form() {
  const [person, setPerson] = useState({
    firstName: " Ahmed ",
    lastName: " Sammak ",
    email: " example@gmail.com ",
  });

  function handleFirstName(e) {
    setPerson({
      ...person,
      firstName: e.target.value,
    });
  }

  function handleLastName(e) {
    setPerson({
      ...person,
      lastName: e.target.value,
    });
  }

  function handleEmail(e) {
    setPerson({
      ...person,
      email: e.target.value,
    });
  }
  return (
    <>
      <label>
        First Name :
        <input value={person.firstName} onChange={handleFirstName} />
      </label>
      <label>
        Last Name :
        <input value={person.lastName} onChange={handleLastName} />
      </label>
      <label>
        First Name :
        <input value={person.email} onChange={handleEmail} />
      </label>
      <p>
        Hello Mr/s {person.firstName}
        {person.lastName} We taked your email {person.email} ,, we wiil email
        you sooooon !
      </p>
    </>
  );
}
