import { useState } from "react";
export default function LovePage() {
  const [name, setName] = useState("");
  const [opinion, setOpinion] = useState("");
  const [testmonial, setTestmonial] = useState({ Oname: "", Oopinion: "" });
  function handleSubmit(e) {
    e.preventDefault();
    setTestmonial({
      Oname: { name },
      Oopinion: { opinion },
    });
  }

  return (
    <>
      <div className="love-st">
        <div className="love-line"></div>
        <p className="love-parag-st">add your comment</p>
        <div className="stars"></div>
      </div>
      <form className="love-form" onSubmit={handleSubmit}>
        <input
          placeholder="Your Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
        <textarea
          placeholder="Write Something"
          value={opinion}
          onChange={(e) => setOpinion(e.target.value)}
        ></textarea>
        <input type="submit" value="SUBMIT" />
      </form>
      <div>
        <h1>{testmonial.Oname}</h1>
        <p>{testmonial.Oopinion}</p>
      </div>
    </>
  );
}
