import { useState } from "react";
export default function LovePage() {
  const [name, setName] = useState("");
  const [opinion, setOpinion] = useState("");
  function handleSubmit(e) {
    e.preventDefault();
    let newName = e.target.value;
    setName(newName);
    console.log(name, opinion);
  } /*
  function handleName(e) {
      let newName = e.target.value;
      setName(newName);
    }
    */
  function handleOpinion(e) {
    let newOpinion = e.target.value;
    setOpinion(newOpinion);
  }
  return (
    <>
      <div className="love-st">
        <div className="love-line"></div>
        <p className="love-parag-st">add your comment</p>
        <div className="stars"></div>
      </div>
      <form className="love-form" onSubmit={handleSubmit}>
        <input placeholder="Your Name" value={name} onChange={handleSubmit} />
        <textarea
          placeholder="Write Something"
          value={opinion}
          onChange={handleOpinion}
        ></textarea>
        <input type="submit" value="SUBMIT" />
      </form>
      <div>
        <h1>{name}</h1>
        <p>{opinion}</p>
      </div>
    </>
  );
}
