import { useState } from "react";

export default function ArrOp() {
  const [name, setName] = useState("");
  const [arr, setArr] = useState([]);
  let nextId = 0;
  function handleAdd(e) {
    setArr([...arr, { name: e.target.value, id: nextId++ }]);
  }
  return (
    <>
      <input
        value={name}
        onChange={(e) => setName(e.target.value)}
        placeholder="Enter the menu element"
      />
      <button onClick={handleAdd}>Add</button>
      <ul>
        {arr.map((arelement) => (
          <li key={arelement.id}>{arelement.name}</li>
        ))}
      </ul>
    </>
  );
}
