import { useState } from "react";

export default function ShapeRendering() {
  let initialShape = [
    { id: 1, type: "square", x: 50, y: 100 },
    { id: 2, type: "circle", x: 150, y: 100 },
    { id: 3, type: "circle", x: 200, y: 100 },
  ];

  const [shape, setShape] = useState(initialShape);

  function changeShapeLocation() {
    setShape([
      shape.map((newShape) => {
        if (shape.type == "square") {
          return shape;
        } else {
          return {
            ...shape,
            y: shape.y + 50,
          };
        }
      }),
    ]);
  }
}
