import { useState } from "react";

export default function ArrOp() {
  const [name, setName] = useState("");
  const [arr, setArr] = useState([]);
  let nextId = 0;

  function handleAdding(e) {
    setArr([...arr, { name: e.target.value, id: nextId++ }]);
  }
  return (
    <>
      <input
        value={name}
        onChange={(e) => setName(e.target.value)}
        placeholder="Enter the menu element"
      />
      <button onClick={handleAdding}>Add</button>
      <ul>
        arr.map(ar =>(
        <li key="ar.id">{ar.name}</li>
        ))
      </ul>
    </>
  );
}
