import { useState } from "react";

function Card() {
  return (
    <div className="love-card">
      <></>
    </div>
  );
}

export default function LovePage() {
  const [name, setName] = useState("");
  const [opinion, setOpinion] = useState("");
  function handleSubmit(e) {
    e.preventDefault();
    setName("");
    setOpinion("");
  }

  return (
    <div className="love-container">
      <div className="love-st">
        <div className="love-line"></div>
        <p className="love-parag-st">add your comment</p>
        <div className="stars">here is stars</div>
      </div>
      <form className="love-form">
        <div className="input-avatar">
          <input
            className="form-input"
            placeholder="Your Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          <p className="avatar">Choose An avatar</p>
        </div>
        <textarea
          className="form-text-area"
          placeholder="Write Something"
          value={opinion}
          onChange={(e) => setOpinion(e.target.value)}
        ></textarea>
        <button onClick={handleSubmit} className="form-buttun">
          SUBMIT
        </button>
      </form>
    </div>
  );
}
