import { useState } from "react";

export default function ArrOp() {
  const [name, setName] = useState("Enter Element");
  const [elements, setElements] = useState([]);
  let nextId = 0;

  return (
    <>
      <input
        value={name}
        onChange={(e) => setName(e.target.value)}
        placeholder="Enter the menu element"
      />
      <button
        onClick={(e) => {
          setElements([{ name: name, id: nextId++ }, ...elements]);
          setName("Enter New Element");
        }}
      >
        Add
      </button>
      <ul>
        {elements.map((element) => (
          <div>
            <li className="hio" key={element.id}>
              {element.name}
            </li>
            <button
              onClick={() => {
                setElements(elements.filter((ele) => ele.id !== element.id));
              }}
            >
              Delete
            </button>
          </div>
        ))}
      </ul>
    </>
  );
}
