import { useState } from "react";

export default function ArrOp() {
  const [name, setName] = useState("");
  const [elements, setElements] = useState([]);
  let nextId = 0;

  return (
    <>
      <input
        value={name}
        onChange={(e) => setName(e.target.value)}
        placeholder="Enter the menu element"
      />
      <button
        onClick={(e) =>
          setElements([{ name: name, id: nextId++ }, ...elements])
        }
      >
        Add
      </button>
      <ul>
        {elements.map((element) => (
          <div>
            <li className="hio" key={element.id}>
              {element.name}
            </li>
            <button
              onClick={elements.filter((element) => element.id !== elements.id)}
            >
              Delete
            </button>
          </div>
        ))}
      </ul>
    </>
  );
}
