import { useState } from "react";

let initialShape = [
  { id: 1, type: "square", x: 50, y: 100 },
  { id: 2, type: "circle", x: 150, y: 100 },
  { id: 3, type: "circle", x: 200, y: 100 },
];
export default function ShapeRendering() {
  const [shape, setShape] = useState(initialShape);

  function changeShapeLocation() {
    setShape([
      shape.map((shape) => {
        if (shape.type === "square") {
          return shape;
        } else {
          return {
            ...shape,
            y: shape.y + 50,
          };
        }
      }),
    ]);
  }
  return (
    <>
      <button onClick={changeShapeLocation}>Move circle</button>
      {shape.map((shapee) => {
        <div
          key={shapee.id}
          style={{
            background: "purple",
            position: "absolute",
            left: shapee.x,
            top: shapee.y,
            borderRadius: shapee.type === "circle" ? "50%" : "",
            width: 20,
            height: 20,
          }}
        />;
      })}
    </>
  );
}
