import { useState } from "react";
import { images } from "./datta";

export default function Guess() {
  const [index, setIndex] = useState(0);
  const [show, setShow] = useState(false);
  let hasNext = index < images.length - 1;
  let hasPrev = index > 0;
  function nextClick() {
    if (hasNext) {
      setIndex(index + 1);
    }
  }
  function previosClick() {
    if (hasPrev) {
      setIndex(index - 1);
    }
  }
  function showClick() {
    setShow(!show);
  }
  let image = images[index];
  return (
    <>
      <button onClick={nextClick} disabled={!hasNext}>
        Next
      </button>
      <button onClick={previosClick} disabled={!hasPrev}>
        previous
      </button>
      <p>
        The {image.name} draw was drawed by : {image.artist}
      </p>
      <img src={image.url} alt={image.alt} />
      <button onClick={showClick}>{show ? "hide" : "show"} Details</button>
      {show && <p>{image.description}</p>}
    </>
  );
}
